import React from "react";
import CartItem from "./cartItem";

class Cart extends React.Component{
    constructor(){
        super()
        this.state={
            Products:[
                {
                    title:'Mobile phone',
                    price:999,
                    qty:1,
                    img:'',
                    id:1
                },
                {
                    title:'Watch',
                    price:99,
                    qty:10,
                    img:'',
                    id:2
                },
                {
                    title:'chocolate',
                    price:9,
                    qty:4,
                    img:'',
                    id:3
                }
            ]
        }
    }
    render(){
        const {Products}=this.state;
        console.log(Products);
        return (
            <div className="cart">
                {Products.map((product)=>{
                    return <CartItem Product={product} 
                    key={product.id} 
                    />
                })}
                
               
            </div>
        )
    }
}

export default Cart;
